import React, { useState } from 'react';
import CardGenerator from './card-logic/CardGenerator';

function App() {
  const [cards, setCards] = useState([]);

  const generateCards = () => {
    const generator = new CardGenerator(5);
    generator.generateCards();
    setCards(generator.cards);
  };

  return (
    <div className="App">
      <button onClick={generateCards}>Generate Cards</button>
      <div className="cards">
        {cards.map((card, index) => (
          <div key={index} className="card">
            <h2>{card.cardName}</h2>
            <img src={card.cardSprite} alt="Card Sprite" />
            <img src={card.cardAccessorySprite} alt="Card Accessory Sprite" />
            <p>Accuracy: {card.Accuracy}</p>
            <p>Retaliation: {card.Retaliation}</p>
            <p>Damage: {card.Damage}</p>
            <p>Health Points: {card.HealthPoints}</p>
            <p>Rarity: {card.abilityRarity}</p>
            <p>Trigger: {card.Trigger}</p>
            <p>Effect: {card.Effect}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
