import React, { useState } from 'react';
import Button from './Button';

const EditParams = ({ isOpen, onClose, initialGlobalCardStats, initialUsePointDistributionSystem, onSave }) => {
  const [localStats, setLocalStats] = useState({ ...initialGlobalCardStats });
  const [usePointDistribution, setUsePointDistribution] = useState(initialUsePointDistributionSystem);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setLocalStats(prevStats => ({
      ...prevStats,
      [name]: value
    }));
  };

  const handleSave = () => {
    onSave(localStats, usePointDistribution);
    onClose();
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-gray-800 bg-opacity-75 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg p-6 max-w-lg w-full">
        <h2 className="text-xl font-bold mb-4 text-center">Edit Generation Parameters</h2>
        <div className="mb-4">
          <h3 className="text-lg font-bold mb-2">Point Distribution System</h3>
          <label>
            <input
              type="checkbox"
              checked={usePointDistribution}
              onChange={(e) => setUsePointDistribution(e.target.checked)}
            /> Enable Point Distribution
          </label>
        </div>
        <div className="mb-4">
          <h3 className="text-lg font-bold mb-2">Global Card Stats</h3>
          <ul className="list-disc list-inside">
            {Object.keys(localStats).map(key => (
              <li key={key}>
                <label>
                  <strong>{key}:</strong>
                  <input
                    type="number"
                    name={key}
                    value={localStats[key]}
                    onChange={handleChange}
                    className="ml-2 p-1 border rounded-md"
                  />
                </label>
              </li>
            ))}
          </ul>
        </div>
        <Button onClick={handleSave} color="blue" className="w-full">
          Save
        </Button>
      </div>
    </div>
  );
};

export default EditParams;
