import React, { useState } from 'react';
import Card from './components/Card';
import InfoModal from './components/InfoModal';
import EditParams from './components/EditParams';
import Button from './components/Button';
import generateRandomCardStats from './card-logic/CardGenerator';
import { generateRandomDefaultCard } from './card-logic/DefaultCardData';
import { globalCardStats as initialGlobalCardStats, usePointDistributionSystem as initialUsePointDistributionSystem } from './card-logic/CardStats';
import { Toaster } from 'react-hot-toast';

function App() {
  const [card, setCard] = useState(null);
  const [isCustom, setIsCustom] = useState(true);
  const [customText, setCustomText] = useState('');
  const [customImage, setCustomImage] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isEditParamsOpen, setIsEditParamsOpen] = useState(false);
  const [cardHistory, setCardHistory] = useState([]);
  const [globalCardStats, setGlobalCardStats] = useState(initialGlobalCardStats);
  const [usePointDistributionSystem, setUsePointDistributionSystem] = useState(initialUsePointDistributionSystem);

  const handleImageUpload = (e) => {
    setCustomImage(URL.createObjectURL(e.target.files[0]));
  };

  const handleCustomTextChange = (e) => {
    setCustomText(e.target.value);
  };

  const updateCardHistory = (newCard) => {
    setCardHistory(prevHistory => {
      const updatedHistory = [newCard, ...prevHistory];
      return updatedHistory.slice(0, 12); // Keep only the last 12 cards including active card
    });
  };

  const generateCustomCard = () => {
    const newCard = generateRandomCardStats(globalCardStats, usePointDistributionSystem);
    newCard.cardName = customText;
    newCard.customSprite = customImage;
    setCard(newCard);
    updateCardHistory(newCard);
  };

  const generateDefaultCard = () => {
    const defaultCard = generateRandomDefaultCard();
    const newCard = generateRandomCardStats(globalCardStats, usePointDistributionSystem);
    newCard.cardName = defaultCard.name;
    newCard.cardSprite = `${process.env.PUBLIC_URL}/cardImages/${defaultCard.filePath}`;
    setCard(newCard);
    updateCardHistory(newCard);
  };

  const handleSaveParams = (newStats, newUsePointDistribution) => {
    setGlobalCardStats(newStats);
    setUsePointDistributionSystem(newUsePointDistribution);
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-start bg-gray-200 p-4">
      <Toaster />

      <div className="mb-8 flex">
        <Button
          onClick={() => setIsCustom(true)}
          color="blue"
          isActive={isCustom}
          className="mr-4"
        >
          Custom Cards
        </Button>
        <Button
          onClick={() => setIsCustom(false)}
          color="green"
          isActive={!isCustom}
        >
          Default Cards
        </Button>
        <Button
          onClick={() => setIsModalOpen(true)}
          color="yellow"
          className="ml-4"
        >
          Info
        </Button>
        <Button
          onClick={() => setIsEditParamsOpen(true)}
          color="yellow"
          className="ml-4"
        >
          Edit Generation
        </Button>
      </div>

      {isCustom ? (
        <div className="mb-8">
          <input
            id="file-upload"
            type="file"
            accept="image/png, image/jpeg, image/jpg"
            onChange={handleImageUpload}
          />
          <input
            type="text"
            placeholder="Enter Card Name"
            value={customText}
            onChange={handleCustomTextChange}
            className="mb-4 p-2 border rounded-md mr-1"
          />
          <Button
            onClick={generateCustomCard}
            color="blue"
          >
            Generate Custom Card
          </Button>
        </div>
      ) : (
        <Button
          onClick={generateDefaultCard}
          color="green"
          className="mb-10"
        >
          Generate New Card
        </Button>
      )}

      {card && (
        <Card card={card} />
      )}

      <hr className="mt-4 w-full border-2 border-blue-500" />

      <h2 className="text-2xl font-bold my-4">Card History</h2>
      <div className="flex overflow-x-auto mt-8 pb-4 space-x-4 w-full">
        <div className="flex space-x-4 items-center">
          {cardHistory.slice(1).map((card, index) => (
            <React.Fragment key={index}>
              {index > 0 && <div className="h-full border-l-2 border-blue-500 mx-4"></div>}
              <div className="flex-shrink-0">
                <Card card={card} />
              </div>
            </React.Fragment>
          ))}
        </div>
      </div>

      <InfoModal isOpen={isModalOpen} onClose={() => setIsModalOpen(false)} />
      <EditParams 
        isOpen={isEditParamsOpen} 
        onClose={() => setIsEditParamsOpen(false)} 
        initialGlobalCardStats={globalCardStats} 
        initialUsePointDistributionSystem={usePointDistributionSystem} 
        onSave={handleSaveParams}
      />
    </div>
  );
}

export default App;
