import React, { useState } from 'react';
import generateRandomCard from './card-logic/CardGenerator';

function App() {
  const [card, setCard] = useState(null);

  const generateCard = () => {
    const newCard = generateRandomCard();
    setCard(newCard);
  };

  return (
    <div className="App">
      <button onClick={generateCard}>Generate Card</button>
      {card && (
        <div className="card">
          <h2>{card.cardName}</h2>
          <img src={card.cardSprite} alt="Card Sprite" />
          <p>Accuracy: {card.Accuracy}</p>
          <p>Retaliation: {card.Retaliation}</p>
          <p>Damage: {card.Damage}</p>
          <p>Health Points: {card.HealthPoints}</p>
          <p>Rarity: {card.abilityRarity}</p>
          <p>Trigger: {card.Trigger}</p>
          <p>Effect: {card.Effect}</p>
        </div>
      )}
    </div>
  );
}

export default App;
