import React, { createContext, useContext, useState } from 'react';

// The card class with information about the card
export class Card {
  constructor() {
    this.cardName = "";
    this.cardSprite = null;
    this.AccuracyPoints = 0;
    this.DefencePoints = 0;
    this.DamagePoints = 0;
    this.HealthPoints = 0;
    this.rarity = null; 
    this.Trigger = "";
    this.Effect = "";
  }
}

// Initial values for context
const initialGlobalCardStats = {
  healthRange: [4, 4],
  damageRange: [4, 4],
  defenceRange: [4, 4],
  accuracyRange: [4, 4],
  distributionPoints: 12
};
const initialGlobalCardStats2 = {
  healthRange: [4, 4],
  damageRange: [4, 4],
  defenceRange: [4, 4],
  accuracyRange: [4, 4],
  distributionPoints: 12
};

const initialUsePointDistributionSystem = true;

// Create context
const CardStatsContext = createContext();

export const CardStatsProvider = ({ children }) => {
  const [globalCardStats, setGlobalCardStats] = useState({ ...initialGlobalCardStats });
  const [usePointDistributionSystem, setUsePointDistributionSystem] = useState(initialUsePointDistributionSystem);

  const resetCardStats = () => {
    console.log('Resetting card stats in context ...');
    setGlobalCardStats({ ...initialGlobalCardStats2 }); // Spread to ensure a new object reference
    setUsePointDistributionSystem(initialUsePointDistributionSystem);
    console.log('Card stats should have been reset here:', initialGlobalCardStats);
  };

  return (
    <CardStatsContext.Provider value={{ 
                                      globalCardStats, setGlobalCardStats, 
                                      usePointDistributionSystem, setUsePointDistributionSystem, 
                                      resetCardStats 
                                      }}>
      {children}
    </CardStatsContext.Provider>
  );
};

export const useCardStats = () => useContext(CardStatsContext);
