import { TextAndWeight } from "./Card";


MaxAccuracy: number;


export class GlobalCardStats {
  constructor() {
    this.MaxAccuracy = 5;
    this.MinAccuracy = 1;
    this.MaxRetaliation = 5;
    this.MinRetaliation = 1;
    this.MaxDamage = 4;
    this.MinDamage = 1;
    this.MaxHealthPoints = 5;
    this.MinHealthPoints = 2;
    this.CommonChance = 5;
    this.UncommonChance = 6;
    this.RareChance = 7;
    this.RandomNames = ["Name1", "Name2", "Name3"];
    this.cardSprites = ["sprite1.png", "sprite2.png", "sprite3.png"];
    this.cardAccessorySprites = ["accessory1.png", "accessory2.png", "accessory3.png"];
    this.CommonTriggers = [new TextAndWeight("Common Trigger 1", 5)];
    this.CommonEffects = [new TextAndWeight("Common Effect 1", 5)];
    this.UncommonTriggers = [new TextAndWeight("Uncommon Trigger 1", 6)];
    this.UncommonEffects = [new TextAndWeight("Uncommon Effect 1", 6)];
    this.RareTriggers = [new TextAndWeight("Rare Trigger 1", 7)];
    this.RareEffects = [new TextAndWeight("Rare Effect 1", 7)];
    this.RollCommon = "Roll on the Common effects wheel instead";
    this.RollUnCommon = "Roll on the Uncommon effects wheel instead";
    this.RollRare = "Roll on the Rare effects wheel instead";
  }
}
